INSERT INTO fond_api_item (title, image, description, amount, full_price, collected, category_id_id) VALUES 

-- Item 1: FPV-дрони (Category: Drones - ID 5)
('FPV-дрони', '', 'FPV дрон — безпілотний літальний апарат First-Person-View (яким керують від першої особи). Завдяки пульту управління та окулярам пілот може швидко й оперативно маневрувати та знаходити ціль для ураження. Такі дрони мають широкий та ефективний спектр застосування: від ураження піхоти і відбиття штурму до знешкодження важкої бронетехніки.', 60, 1440000, 0, 5),

-- Item 2: Дрони "Очі в небі" (Category: Drones - ID 5)
('Дрони "Очі в небі" (drones.jpg)', '', 'Дрони - це очі сучасної армії. З їх допомогою, військові розвідують, коригують, роблять "смерть ворогам", збивають ворожі дрони й навіть беруть у полон! А все тому, що наші на війні використовують всю техніку, яку отримують, максимально ефективно.', 30, 800000, 0, 5),

-- Item 3: Лелеки (Category: UAV - ID 4)
('Лелеки (leleka.jpg)', '', 'Лелека-100 (анг. Leleka-100 або Ciconia) — український безпілотний літальний апарат, призначений для ведення розвідки. Створений українською компанією DeViRo. Прийнятий на озброєння Збройних Сил України в травні 2021 року.', 20, 1360000, 0, 4),

-- Item 4: Shark-D (Category: UAV - ID 4)
('Shark-D (Shark.jpg)', '', 'SHARK (укр. «ШАРК») — розвідувальний безпілотний комплекс, створений компанією Ukrspecsystems для спостереження та коригування вогню. Безпілотний літальний апарат (БпЛА) обладнано високонадійним модулем зв''язку (шифрування AES256), що дозволяє йому заглибитися у тил противника до 80 км в умовах радіоелектронної боротьби (РЕБ).', 5, 600000, 0, 4),

-- Item 5: Позашляховики (Category: Transport - ID 1)
('Позашляховики (big-auto.jpg)', '', 'Позашляховик (англ. off-road vehicle, all-road vehicle, нім. Geländewagen) — повнопривідний (4×4) легковий чи легкий комерційний автомобіль підвищеної прохідності для експлуатації на шляхах всіх рівнів, а також на бездоріжжі. «Автомобіль підвищеної прохідності» є офіційним терміном. Від звичайних легкових машин позашляховик відрізняється конфігурацією.', 12, 15000000, 0, 1),

-- Item 6: Човни (Category: Transport - ID 1)
('Човни (boats.jpg)', '', 'Надувні човни мають основну беззаперечну перевагу перед жорсткими човнами – легкість та мобільність. У складеному вигляді вони можуть перевозитися в багажнику автомобіля або переноситися в спеціальній сумці або рюкзаку. Та й надутий, повністю оснащений човен ПВХ можуть переносити на невелику відстань 5-6 людей.', 8, 4700000, 0, 1),

-- Item 7: FV432 (Category: Transport - ID 1)
('FV432 (fv-432)', '', 'FV432 — британський бронетранспортер серії FV430. З часу його створення у 1960, він став найпоширенішим варіантом бронетехніки, яка використовувалася для перевезення піхоти на полі бою. У 1980-х використовувалося майже 2500 машин і до тепер близько 1500 залишаються на службі як допоміжний транспорт. Хоча серію FV432 повинні були замінити нові машини, такі як Warrior та CVR(T), нині вони проходять оновлення, щоб нести службу й у наступному десятилітті.', 3, 5800000, 0, 1),

-- Item 8: Тепловізори (Category: Optical devices - ID 3)
('Тепловізори (teplovizor.jpg)', '', 'Тепловізор (інфрачервона камера) — оптико-електронний прилад для візуалізації температурного поля та вимірювання температури. Переважно працює в інфрачервоній частині електромагнітного спектру — теплові зображення утворюються завдяки зміщенню максимумів спектрів власного випромінювання тіл під час їх нагрівання у короткохвильову область.', 40, 7200000, 0, 3),

-- Item 9: Портативні цифрові рації (Category: Communication - ID 2)
('Портативні цифрові рації (motorbo-trade-in.png)', '', 'Портативна радіостанція, також відома як радіоапаратура, радіостанція або просто рація, є пристроєм для бездротового зв''язку, який дозволяє обмінюватися голосовими повідомленнями або іншою інформацією між користувачами на певній відстані. Вона часто використовується для спілкування та координації між людьми, які перебувають у межах дії радіосигналу.', 40, 1200000, 0, 2),

-- Item 10: PD-1 (Category: UAV - ID 4)
('PD-1 (pd-1.jpg)', '', 'PD-1 (акр. від англ. People''s Drone — «народний дрон») — український безпілотний авіаційний комплекс, призначений для здійснення повітряної розвідки та контролю над переміщенням супротивника. Розробкою та випуском займається Всеукраїнський центр волонтерів «Народний проект» та Ukrspecsystems.', 7, 7480000, 0, 4),

-- Item 11: H10 Poseidon (Category: UAV - ID 4)
('H10 Poseidon (poseidon)', '', 'H10 Poseidon — розвідувальний безпілотний літальний апарат, який виготовляє компанія Swarmly, оснащені двома камерами із 40-кратним зумом з захищеними каналами зв''язку, вертикальною системою зліту й посадки.', 12, 6000000, 0, 4),

-- Item 12: RAM II UAV (Category: UAV - ID 4)
('RAM II UAV (ram2.jpg)', '', 'RAM II UAV — український дрон-камікадзе. Створений на базі корпусу БПЛА «Лелека-100» компанії DEVIRO. Система призначена для нанесення точних ефективних ударів по ворожих силах та мінімізації супутнього збитку при використанні в міській місцевості. Запуск комплексу здійснюється з мобільної катапульти.', 14, 5600000, 0, 4),

-- Item 13: Shark (Category: UAV - ID 4)
('Shark (Shark_5ss1Na0.jpg)', '', 'SHARK(укр. «ШАРК») — розвідувальний безпілотний комплекс, створений компанією Ukrspecsystems для спостереження та коригування вогню. Безпілотний літальний апарат (БпЛА) обладнано високонадійним модулем зв''язку (шифрування AES256), що дозволяє йому заглибитися у тил противника до 80 км в умовах радіоелектронної боротьби (РЕБ).', 8, 8000000, 0, 4),

-- Item 14: Starlink (Category: Communication - ID 2)
('Starlink (starlink.jpeg)', '', 'Starlink — проєкт американської компанії SpaceX щодо розробки високопродуктивної супутникової платформи для виготовлення супутників зв''язку та запусків великої їх кількості (сузір''я) у космос. Система надає доступ до широкосмугового інтернету у будь-якій точці планети. Першою компанія почала обслуговувати Канаду та США (2020). Назва «Starlink» взята із роману Джона Ґріна «Провина зірок». Проєкт почався у 2015 році, а перші два супутники було запущено у тестовий політ 22 лютого 2018 року ракетою Falcon 9.', 8, 240000, 0, 2),

-- Item 15: Прилади нічного бачення Gen. 3 (Category: Optical devices - ID 3)
('Прилади нічного бачення Gen. 3 (gen3_....png)', '', 'Gen 3 (третє покоління пнб) - це прилади нічного бачення, створені в 1990-х роках, які мають ще більшу роздільну здатність та якість зображення, а також ширший діапазон виявлення об''єктів. Вони також мають більш тривалий термін служби та нижчий рівень шумів. У третьому поколінні ПНБ використовується фотокатод на основі арсеніду галію, що істотно збільшує чутливість приладу на порядок.', 20, 7200000, 0, 3);

